const saveToken = async (token) => {
    sessionStorage.setItem("token", token);
};

const loadToken = async () => {
    const currentJWT = sessionStorage.getItem("token")
    if (currentJWT) {
        return currentJWT;
    }
    else {
        console.log("no token");
    }
};

const clearToken = async () => {
    sessionStorage.removeItem("token");
};

const addAuthentication = async () => {
    sessionStorage.setItem("auth", true);
}

const getAuthentication = async () => {
    const auth = sessionStorage.getItem("auth");
    return auth;
}

const removeAuthentication = async () => {
    sessionStorage.setItem("auth", false);
}

export { saveToken, loadToken, clearToken, addAuthentication, getAuthentication, removeAuthentication }
